    /**
     * execution a function specified in the json.  The implementations for functions must be specified in this
     * class and must match the signature of the functions in the json
     *
     * @param functionName name of the private function in this class
     * @param arguments    the arguments for the function int his class
     * @return the results of executing the function.
     * @throws IllegalArgumentException
     */
    @Override
    public String execute(String functionName, Object... arguments) throws IllegalArgumentException {
        List<Class> classList = new ArrayList<Class>();
        if (arguments != null) {
            for (Object argument : arguments) {
                classList.add(argument.getClass());
            }
        }
        try {
            Method method = getMethod(this.getClass(), functionName, classList.toArray(new Class[classList.size()]));
            return method.invoke(this, arguments).toString();
        } catch (SecurityException e) {
            throw new IllegalArgumentException(e);
        } catch (NoSuchMethodException e) {
            try {
                Method method = getMethod(this.getClass(), functionName, Object[].class);
                return method.invoke(this, new Object[]{arguments}).toString();
            } catch (NoSuchMethodException e1) {
                throw new IllegalArgumentException(e);
            } catch (InvocationTargetException e1) {
                throw new IllegalArgumentException(e);
            } catch (IllegalAccessException e1) {
                throw new IllegalArgumentException(e);
            }
        } catch (InvocationTargetException e) {
            throw new IllegalArgumentException(e);
        } catch (IllegalAccessException e) {
            throw new IllegalArgumentException(e);
        } catch (Exception e) {
            throw new IllegalArgumentException(e);
        }
    }

    private Method getMethod(Class clazz, String functionName, Class<?>... parameterTypes) throws NoSuchMethodException {
        Set<Class<?>> setOfClasses = ImmutableSet.copyOf(Arrays.asList(parameterTypes));
        try {
            return clazz.getDeclaredMethod(functionName, parameterTypes);
        } catch (NoSuchMethodException e) {
            return clazz.getDeclaredMethod(RESERVED + functionName, parameterTypes);
        }
    }

